import React, { useEffect, useMemo, useState } from 'react'
import { useParams } from 'react-router-dom';
import Slider from 'react-slick'
import { recommendedServiceAction } from '../../store/slices/recommendedServiceSlice';
import { useAppDispatch, useAppSelector } from '../../store/storeHooks';

const RecommendedService = () => {

    const { serviceId } = useParams();

    const dispatch = useAppDispatch();

    const serviceDetailsPage = useAppSelector((a) => a.serviceDetailsPageDetails)
    // console.log("serviceDetailspage--->", serviceDetailsPage)

    const [categoryId, setCategoryId] = useState<string>();
    // console.log("categoryId-->", categoryId)
    const [subCategoryId, setSubCategoryId] = useState<string>();
    // console.log("subCategoryId--->", subCategoryId)

    // useEffect(() => {
    //     setCategoryId(serviceDetailsPage.categoryId);
    //     setSubCategoryId(serviceDetailsPage.subcategoryId)
    // })

    const memoValue = useMemo<any>(() => {
        setCategoryId(serviceDetailsPage.categoryId);
        setSubCategoryId(serviceDetailsPage.subcategoryId)
    }, [categoryId, subCategoryId])

    // useEffect(() => {
    //     if (serviceId && categoryId && subCategoryId) {
    //         dispatch(recommendedServiceAction({ serviceId, categoryId, subCategoryId }))
    //     }
    // })

    useEffect(() => {
        if (serviceId && memoValue) {
            dispatch(recommendedServiceAction({ serviceId, memoValue }))
        }
    })



    // for slider
    const settings = {
        // dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 2,
        slidesToScroll: 1
    };

    return (
        <>
            <div className="simple-sliders">
                <div className="slider-heading">
                    <h2>Recommended Services</h2>
                </div>
                <div className='service_block'>
                    <Slider {...settings}>
                        {
                            <h1>Recommended Service</h1>
                            // recommendedData ? recommendedData.map((data, index) => {
                            //     return (
                            //         <RecommendedCardService dataInfo={data} key={index} />
                            //     )
                            // }) : null
                        }

                    </Slider>

                </div>
            </div>
        </>
    )
}

export default RecommendedService
