import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { RootState } from '../../store';
import { apiInstance } from "../../API/axios";
import endPoints from "../../API/endPoints";
import { HomePageType } from "../../types/HomePageTypes";




interface homeState {
    homeData: any;
    isLoading: boolean
}

const initialState: HomePageType | null = null;

// const initialState: homeState = {
//     homeData: {},
//     isLoading: false
// };

export const getHomeData = createAsyncThunk(
    'homedata',
    async () => {
        const response = await apiInstance.get(endPoints.GET_HOME_PAGE).then(
            res => res.data.data
        )
        return response
    }
)

export const homeSlice = createSlice({
    name: 'homeData',
    initialState,
    reducers: {
    },

    extraReducers: (builder) => {
        builder.addCase(getHomeData.fulfilled, (state: any, action: any) => {
            state.homeData = action.payload
        })
    }
});
export const homeData = (state: RootState) => state.homeData.homeData;
export default homeSlice.reducer;
