import React, { useEffect } from "react";
import Slider from "react-slick";
import { getHomeData } from "../../store/slices/homeSlice";
import { useAppDispatch, useAppSelector } from "../../store/storeHooks";
import { bannerItems } from "../../store/Types";

let bannerSetting = {
  infinite: false,
  speed: 500,
  slidesToShow: 1,
  slidesToScroll: 1,
  arrows: false,
};

const HomePageData = () => {
  const dispatch = useAppDispatch();

  const homePageData = useAppSelector((state) => state.homeData.homeData);
  console.log("homePageData==>", homePageData);

  useEffect(() => {
    dispatch(getHomeData());
  }, [dispatch]);

  return (
    <>
      <section className="banner">
        <div className="container">
<div className="banner-wrap">
            <Slider className="overflow-hidden" {...bannerSetting}>
              {homePageData?.bannerData?.map(
                (bannerItem: bannerItems, index: number) => {
                  // console.log("bannerItems==>", bannerItem)
                  return (
                    <>
                      <div className="img-wrap" key={bannerItem._id}>
                        <img
                          src={
                            homePageData.bannerImagePath + bannerItem.webImage
                          }
                        />
                      </div>
                    </>
                  );
                }
              )}
            </Slider>
          </div>
        </div>
      </section>
    </>
  );
};

export default HomePageData;
