import React, { useEffect } from 'react'
import { useSelector } from 'react-redux';
import Slider from "react-slick";
import { RootState } from '../../store';
import { getHomeData } from '../../store/slices/homeSlice';
import { useAppDispatch, useAppSelector } from '../../store/storeHooks';
import { BannerData, HomePageType } from '../../types/HomePageTypes';
import { homeData } from '../../store/slices/homeSlice'

let bannerSetting = {
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
};

const TopBanner = () => {

    const dispatch = useAppDispatch();
    const homePageData: HomePageType = useAppSelector(state => state.getHomePageDetails)
    console.log("homePageData==>", homePageData)

    // console.log("fsdfdsfdsf", homeData)

    useEffect(() => {
        dispatch(getHomeData());
    }, [dispatch])

    return (
        <>
            <section className="banner">
                <div className="container">
                    <div className="banner-wrap">
                        <Slider className="overflow-hidden" {...bannerSetting}>
                            {homePageData?.bannerData?.map(
                                (bannerItem: BannerData, index: number) => {
                                    // console.log("bannerItems==>", bannerItem)
                                    return (
                                        <div className="img-wrap" key={index}>
                                            <img
                                                src={
                                                    homePageData.bannerImagePath + bannerItem.webImage
                                                }
                                            />
                                        </div>
                                    );
                                }
                            )}
                        </Slider>
                    </div>
                </div>
            </section>
        </>
    )
}

export default TopBanner
